x.viewPetitions()
x.vote("0x5eF34402dD52aC15A6dE46490Eda54005EE4038f", {from:"0x54A49513BFdFdfdfe1D2d86709e2E8bFAF68081c"})
x.vote("0x5C626149C2b5d1f199292a4C446D9f667758DfdA", {from:"0x54A49513BFdFdfdfe1D2d86709e2E8bFAF68081c"})
